article .anchor {
  padding-left: 0.5rem;
  opacity: 0;
  transition: opacity 300ms ease-in-out;
}

article :is(h2, h3, h4, h4, h6):hover .anchor {
  opacity: 0.6;
}

details {
  padding: 1rem;
  border: 2px solid #F2CFC2;
}

/* css for loading */
@keyframes loading-svg {
  0%   { cy: 10; }
  25%  { cy: 3; }
  50%  { cy: 10; }
}

.loadingCircle {
  animation-duration: 1s;
  animation-iteration-count: infinite;
  animation-name: loading-svg;
}
.loadingCircle:nth-child(2) { animation-delay: .1s }
.loadingCircle:nth-child(3) { animation-delay: .2s }

.fontOutfit {
  font-family: Outfit, ui-monospace, SFMono-Regular, Menlo, Monaco, Consolas, Liberation Mono, Courier New;
}

.hr {
  display: block;
  border: none;
  height: 5px;
  background: url(/images/wave.svg) 0 repeat-x
}

/* Normalize KaTeX font size to match surrounding body text */
.katex {
  font-size: 1em !important;
}

/* Override Tailwind Typography's backtick pseudo-elements on inline code */
.prose :not(pre) > code::before,
.prose :not(pre) > code::after {
  content: '' !important;
}

/* Inline code: no bold */
.prose :not(pre) > code {
  font-weight: 400 !important;
}

/* Code block copy button */
.remark-highlight {
  position: relative;
}

.remark-highlight .copy-btn {
  position: absolute;
  top: 8px;
  right: 10px;
  opacity: 0;
  transition: opacity 150ms ease;
  font-size: 11px;
  font-family: 'Moderat', sans-serif;
  letter-spacing: 0.5px;
  color: var(--syntax-comment);
  background: transparent;
  border: none;
  cursor: pointer;
  padding: 2px 6px;
  border-radius: 3px;
  line-height: 1.6;
}

.remark-highlight:hover .copy-btn {
  opacity: 1;
}

.remark-highlight .copy-btn:hover {
  color: var(--syntax-text);
}

/* Margin annotations / sidenotes */
.sidenote {
  display: block;
  margin: 0.875rem 0;
  padding-left: 0.75rem;
  border-left: 1.5px solid var(--color-muted);
  font-size: 0.8rem;
  line-height: 1.65;
  color: var(--color-text-secondary);
  font-style: normal;
  font-family: var(--font-moderat), sans-serif;
}

@media (min-width: 1280px) {
  .sidenote {
    float: right;
    clear: right;
    width: 10rem;
    margin-right: -12.5rem;
    margin-left: 1.5rem;
    margin-top: 0.2rem;
    margin-bottom: 0.5rem;
    font-size: 0.7rem;
  }
}

