  .sidenote {
    float: right;
    clear: right;
    width: 14rem;
    margin-right: -16rem;
    margin-left: 1.5rem;
    margin-top: 0.2rem;
    margin-bottom: 0.5rem;
    font-size: 0.7rem;
  }
}

