import { ReactNode } from 'react';

const URL_RE = /(https?:\/\/[^\s]+)/;

function linkify(text: string): ReactNode[] {
  const parts = text.split(URL_RE);
  // split with a capturing group: even indices = plain text, odd = URL matches
  return parts.map((part, i) =>
    i % 2 === 1
      ? <a key={i} href={part} target="_blank" rel="noopener noreferrer">{part}</a>
      : part
  );
}

function linkifyChildren(children: ReactNode): ReactNode {
  if (typeof children === 'string') return linkify(children);
  return children;
}

export function SideNote({ children }: { children: ReactNode }) {
  return <span className="sidenote">{linkifyChildren(children)}</span>;
}

