import { ReactNode } from 'react';

const URL_RE = /(https?:\/\/[^\s]+)/;

function linkify(text: string): ReactNode[] {
  const parts = text.split(URL_RE);
  // split with a capturing group: even indices = plain text, odd = URL matches
  return parts.map((part, i) =>
    i % 2 === 1
      ? <a key={i} href={part} target="_blank" rel="noopener noreferrer">{part}</a>
      : part
  );
}

function linkifyChildren(children: ReactNode): ReactNode {
  if (typeof children === 'string') return linkify(children);
  return children;
}

interface SideNoteProps {
  children: ReactNode;
  /** Original mode: children is the sidenote content */
  anchor?: ReactNode;
  /** asChild mode: children is the annotated text, note is the sidenote content */
  asChild?: boolean;
  note?: string;
}

export function SideNote({ children, anchor, asChild, note }: SideNoteProps) {
  if (asChild) {
    return (
      <>
        <span className="footnote-ref">{children}</span>
        <span className="sidenote">{note ? linkifyChildren(note) : null}</span>
      </>
    );
  }
  return (
    <>
      {anchor && <span className="footnote-ref">{anchor}</span>}
      <span className="sidenote">{linkifyChildren(children)}</span>
    </>
  );
}

