import { ReactNode, useEffect, useRef, useState } from 'react';
import { createPortal } from 'react-dom';

const URL_RE = /(https?:\/\/[^\s]+)/;

function linkify(text: string): ReactNode[] {
  const parts = text.split(URL_RE);
  return parts.map((part, i) =>
    i % 2 === 1
      ? <a key={i} href={part} target="_blank" rel="noopener noreferrer">{part}</a>
      : part
  );
}

function linkifyChildren(children: ReactNode): ReactNode {
  if (typeof children === 'string') return linkify(children);
  return children;
}

interface LineCoords { x1: number; y1: number; x2: number; y2: number }

function ConnectedSideNote({ children, note }: { children: ReactNode; note: string }) {
  const refEl = useRef<HTMLSpanElement>(null);
  const noteEl = useRef<HTMLSpanElement>(null);
  const [line, setLine] = useState<LineCoords | null>(null);
  const [hovered, setHovered] = useState(false);
  const [mounted, setMounted] = useState(false);

  useEffect(() => { setMounted(true); }, []);

  useEffect(() => {
    if (!mounted) return;
    const measure = () => {
      if (!refEl.current || !noteEl.current || window.innerWidth < 1280) {
        setLine(null);
        return;
      }
      const r = refEl.current.getBoundingClientRect();
      const n = noteEl.current.getBoundingClientRect();
      setLine({ x1: r.right, y1: r.top + r.height / 2, x2: n.left, y2: n.top + n.height / 2 });
    };
    measure();
    window.addEventListener('resize', measure);
    window.addEventListener('scroll', measure, { passive: true });
    return () => {
      window.removeEventListener('resize', measure);
      window.removeEventListener('scroll', measure);
    };
  }, [mounted]);

  const onEnter = () => setHovered(true);
  const onLeave = () => setHovered(false);

  return (
    <>
      <span ref={refEl} className="footnote-ref" onMouseEnter={onEnter} onMouseLeave={onLeave}>
        {children}
      </span>
      <span ref={noteEl} className="sidenote" onMouseEnter={onEnter} onMouseLeave={onLeave}>
        {linkifyChildren(note)}
      </span>
      {mounted && line && createPortal(
        <svg
          aria-hidden="true"
          style={{ position: 'fixed', inset: 0, width: '100vw', height: '100vh', pointerEvents: 'none', zIndex: 5 }}
        >
          <line
            x1={line.x1} y1={line.y1} x2={line.x2} y2={line.y2}
            stroke="currentColor"
            strokeWidth={0.75}
            style={{ opacity: hovered ? 0.45 : 0.12, transition: 'opacity 200ms ease' }}
          />
        </svg>,
        document.body
      )}
    </>
  );
}

interface SideNoteProps {
  children: ReactNode;
  anchor?: ReactNode;
  asChild?: boolean;
  note?: string;
}

export function SideNote({ children, anchor, asChild, note }: SideNoteProps) {
  if (asChild && note) {
    return <ConnectedSideNote note={note}>{children}</ConnectedSideNote>;
  }
  return (
    <>
      {anchor && <span className="footnote-ref">{anchor}</span>}
      <span className="sidenote">{linkifyChildren(children)}</span>
    </>
  );
}

