     8import { formateDatePreview, validDate } from '@/utils/formatDate';
     9import { getAllDocsMeta, PostPath } from '@/utils/loadMDX';
    10
    11export const getStaticProps = async () => {
    12  const posts = await getAllDocsMeta(PostPath);
    13  return { props: { posts } };
    14};
    15
    16export const formatTags = (tags: string) => {
    17  const formattedTagsArr: string[] = [];
    18  for (const tagStr of tags.split(',')) {
    19    formattedTagsArr.push(`#${tagStr}`);
    20  }
    21  return formattedTagsArr;
    22};
    23
    24export const PostPreview: React.FC<PostMeta> = ({ slug, title, date, language }) => {
    25  const router = useRouter();
    26
    27  return (
    28    <li className="my-1">
    29      <Link
    30        href={`${router.asPath}posts/${slug}`}
    31        className="flex items-center p-1 capitalize transition-colors duration-200 rounded outline-none opacity-70 hover:opacity-100 hover:font-black">
    32        <p className="text-sm mr-8 min-w-[50px] whitespace-nowrap">
    33          <time dateTime={validDate(date)}>{formateDatePreview(date)}</time>
    34        </p>
    35        <h3 className="font-normal">{title}</h3>
    36        <p className="ml-2 text-xs bg-black opacity-100 text-white dark:bg-white dark:text-black pl-1 pr-1">
    37          {language}
    38        </p>
    39      </Link>
    40    </li>
    41  );
    42};
    43
    44export const PostPreviewList: React.FC<{ posts: PostMeta[] }> = ({ posts }) => {
    45  const { tags: selectedTags } = useTags();
    46  const showAllPosts = selectedTags.size === 0;
    47  const postTagCountMap = posts.reduce((tagCountMap, post) => {
    48    formatTags(post.tags).forEach((tag) => tagCountMap.set(tag, (tagCountMap.get(tag) ?? 0) + 1));
    49    return tagCountMap;
    50  }, new Map());
    51
    52  const fileredPosts = showAllPosts
    53    ? posts
    54    : posts.filter((post) => {
    55        const postTagSet = new Set(formatTags(post.tags));
    56        return Array.from(selectedTags).every((selectedTag) => postTagSet.has(selectedTag));
    57      });
    58
    59  if (!showAllPosts && fileredPosts.length === 0) {
    60    return (
    61      <>
    62        <TagList postTagCountMap={postTagCountMap} />
    63        <ResetTagsButton />
    64      </>
    65    );
    66  }
    67
    68  const postsByYear: Record<string, PostMeta[]> = {};
    69
    70  fileredPosts.forEach((post) => {
    71    const year = new Date(post.date).getFullYear();
    72    const knownPosts = postsByYear[year] || [];
    73    postsByYear[year] = [...knownPosts, post];
    74  });
    75
    76  return (
    77    <>
    78      {Object.entries(postsByYear)
    79        .reverse()
    80        .map(([year, posts], idx) => {
    81          return (
    82            <div key={year} className="w-full">
    83              <h2 className={`pl-1 text-lg font-catamaran ${idx !== 0 ? 'mt-4' : ''}`}>{year}</h2>
    84              <ul className="font-overpass font-medium opacity-90">
    85                {posts.map((post) => {
    86                  return <PostPreview key={post.slug} {...post} />;
    87                })}
    88              </ul>
    89            </div>
    90          );
    91        })}
    92    </>
    93  );
    94};
    95
    96type Props = InferGetStaticPropsType<typeof getStaticProps>;
    97
    98const Posts: React.FC<Props> = ({ posts }) => {
    99  return (
   100    <>
   101      <NextSeo
   102        title="Posts"
   103        canonical="https://www.nazha.co/posts"
   104        openGraph={{ url: 'https://www.nazha.co/posts' }}
