     1import fs from 'fs';
     2import matter from 'gray-matter';
     3import { bundleMDX } from 'mdx-bundler';
     4import path from 'path';
     5import rehypeAutolink from 'rehype-autolink-headings';
     6import rehypeSlug from 'rehype-slug';
     7import remarkGfm from 'remark-gfm';
     8import remarkPrism from 'remark-prism';
     9import glob from 'tiny-glob';
    10
    11import remarkHighlightSyntax from './hightlight';
    12import { autoLinkHeadingsOptions } from './rehypeAutolinkPlugin';
    13
    14export const RootPath = process.cwd();
    15export const PostPath = path.join(RootPath, 'posts');
    16export const ExercisesPath = path.join(RootPath, 'exercises');
    17
    18export async function loadMDX(source: string) {
    19  const bundle = await bundleMDX({
    20    source,
    21    xdmOptions: (options) => {
    22      options.remarkPlugins = [
    23        ...(options?.remarkPlugins ?? []),
    24        remarkGfm,
    25        remarkPrism,
    26        remarkHighlightSyntax
    27      ];
    28      options.rehypePlugins = [
    29        ...(options?.rehypePlugins ?? []),
    30        rehypeSlug,
    31        [rehypeAutolink, autoLinkHeadingsOptions]
    32      ];
    33      return options;
    34    }
    35  });
    36
    37  return bundle;
    38}
    39
    40/**
    41 * Get meta data of all posts
    42 */
    43export const getAllDocsMeta = async (dir: string) => {
    44  const allDocsMeta = await glob(`${dir}/**/*.mdx`);
    45
    46  return allDocsMeta
    47    .map((postPath): PostMeta => {
    48      const post = fs.readFileSync(path.join(RootPath, postPath), 'utf-8');
    49
    50      const slug = path.basename(postPath).replace('.mdx', '');
    51      const meta = matter(post).data;
    52
    53      return { ...meta, slug } as PostMeta;
    54    })
    55    .filter((meta) => meta.published)
    56    .sort((a, b) => Number(new Date(b.date)) - Number(new Date(a.date)));
    57};
    58
    59const TWEET_RE = /<StaticTweet\sid="[0-9]+"\s\/>/g;
    60
    61/**
    62 * Get a single post content by slug
    63 */
    64
    65export const getDocs = async (slug: string, dir: string) => {
    66  const source = fs.readFileSync(path.join(dir, `${slug}.mdx`), 'utf-8');
    67
    68  const { code, frontmatter, matter } = await loadMDX(source);
    69
    70  const tweetMatch = matter.content.match(TWEET_RE);
    71
    72  const tweetIDs = tweetMatch?.map((mdxTweet) => {
    73    const id = mdxTweet.match(/[0-9]+/g)![0];
    74    return id;
    75  });
    76
    77  const meta = { ...frontmatter, slug } as PostMeta;
    78  return { meta, code, tweetIDs: tweetIDs ?? [] };
    79};
