     3export const formateDatePreview = (d: string): string => {
     4  const date = new Date(d);
     5
     6  const month = localeFormat(date, '{MMM}', 'en-GB');
     7  const _date = format(date, '{dd}');
     8
     9  return `${month} ${_date} `;
    10};
    11
    12export const formateDateFull = (d: string): string => {
