import { format } from 'light-date';
import { InferGetStaticPropsType } from 'next';
import Link from 'next/link';
import { NextSeo } from 'next-seo';

import { getAllDocsMeta, PostPath } from '@/utils/loadMDX';

export const getStaticProps = async () => {
  const posts = await getAllDocsMeta(PostPath);
  return { props: { posts } };
};

const formatDateShort = (d: string): string => {
  const date = new Date(d);
  return `${format(date, '{dd}')}/${format(date, '{MM}')}`;
};

const isRecentPost = (d: string): boolean => {
  const diffMs = Date.now() - new Date(d).getTime();
  return diffMs < 30 * 24 * 60 * 60 * 1000;
};

const NewBadge = () => (
  <span
    className="ml-2 relative inline-flex items-center justify-center flex-shrink-0"
    style={{ width: 46, height: 22, transform: 'rotate(-2deg)', verticalAlign: 'middle' }}>
    <svg
      className="absolute inset-0"
      width="46"
      height="22"
      viewBox="0 0 46 22"
      fill="none"
      xmlns="http://www.w3.org/2000/svg">
      <path
        d="M5 11C5.5 5 12 1.5 23 2C34 2.5 41.5 5.5 41 11C40.5 16.5 33 20.5 22 20C11 19.5 4.5 17 5 11Z"
        stroke="#ec4899"
        strokeWidth="1.3"
      />
    </svg>
    <span className="relative text-xs text-pink-500 leading-none font-normal">New</span>
  </span>
);

type PostRowProps = {
  post: PostMeta;
  showYear: boolean;
};

const PostRow: React.FC<PostRowProps> = ({ post, showYear }) => {
  const year = new Date(post.date).getFullYear();
  const showBadge = isRecentPost(post.date);

  return (
    <div className="flex items-center border-t border-gray-200 dark:border-gray-800 py-2.5">
      <span className="w-14 text-sm text-gray-400 dark:text-gray-500 flex-shrink-0 tabular-nums">
        {showYear ? year : ''}
      </span>
      <Link
        href={`/posts/${post.slug}`}
        className="flex-1 flex items-center min-w-0 text-sm hover:opacity-60 transition-opacity duration-150">
        <span className="truncate">{post.title}</span>
        {showBadge && <NewBadge />}
      </Link>
      <span className="text-sm text-gray-400 dark:text-gray-500 tabular-nums ml-4 flex-shrink-0">
        {formatDateShort(post.date)}
      </span>
    </div>
  );
};

const PostList: React.FC<{ posts: PostMeta[] }> = ({ posts }) => {
  const postsByYear: Record<string, PostMeta[]> = {};

  posts.forEach((post) => {
    const year = new Date(post.date).getFullYear().toString();
    postsByYear[year] = [...(postsByYear[year] || []), post];
  });

  return (
    <div className="w-full">
      {Object.entries(postsByYear)
        .sort(([a], [b]) => Number(b) - Number(a))
        .map(([, yearPosts]) =>
          yearPosts.map((post, idx) => (
            <PostRow key={post.slug} post={post} showYear={idx === 0} />
          ))
        )}
    </div>
  );
};

type Props = InferGetStaticPropsType<typeof getStaticProps>;

const Home: React.FC<Props> = ({ posts }) => {
  return (
    <>
      <NextSeo
        title="nazha"
        canonical="https://www.nazha.co"
        openGraph={{ url: 'https://www.nazha.co' }}
      />
      <div className="w-full sm:max-w-[600px] mx-auto px-6 pt-16 pb-24">
        {/* Header */}
        <div className="mb-10">
          <p className="text-sm text-gray-500 dark:text-gray-400">nazha</p>
          <p className="text-xs text-gray-400 dark:text-gray-500 mt-0.5">
            Updated Feb 19, 2026
          </p>
        </div>

        {/* Bio */}
        <div className="mb-16 space-y-4 text-sm leading-relaxed text-gray-700 dark:text-gray-300">
          <p>I build software and write about it on the internet.</p>
          <p>
            I created{' '}
            <Link
              href="https://github.com/maoxiaoke/r2Uploader"
              className="underline underline-offset-2">
              R2Uploader
            </Link>
            , a desktop client for Cloudflare R2 storage, and{' '}
            <Link
              href="https://github.com/maoxiaoke/BaiJi-releases"
              className="underline underline-offset-2">
              BaiJi
            </Link>
            , a macOS app. I also maintain{' '}
            <Link href="https://hnta.nazha.co" className="underline underline-offset-2">
              Hacker News Top Archive
            </Link>{' '}
            and{' '}
            <Link
              href="https://chromewebstore.google.com/detail/candy/hnmedbpaedahbjkgdionefljphkglpco"
              className="underline underline-offset-2">
              Candy
            </Link>
            , a Chrome extension.
          </p>
          <p>
            I write a{' '}
            <Link href="https://quaily.com/nazha" className="underline underline-offset-2">
              newsletter
            </Link>{' '}
            and tend a{' '}
            <Link href="https://garden.nazha.co" className="underline underline-offset-2">
              digital garden
            </Link>
            .
          </p>
          <p>
            You can reach me at{' '}
            <Link href="https://twitter.com/xiaokedada" className="underline underline-offset-2">
              @xiaokedada
            </Link>
            .
          </p>
        </div>

        {/* Writing */}
        <div>
          <p className="text-sm text-gray-400 dark:text-gray-500 mb-0">Writing</p>
          <PostList posts={posts} />
        </div>
      </div>
    </>
  );
};

export default Home;

