import { motion, useReducedMotion } from 'framer-motion';
import { format } from 'light-date';
import { InferGetStaticPropsType } from 'next';
import Link from 'next/link';
import { NextSeo } from 'next-seo';

import { getAllDocsMeta, PostPath } from '@/utils/loadMDX';

export const getStaticProps = async () => {
  const posts = await getAllDocsMeta(PostPath);
  return { props: { posts } };
};

const formatDateShort = (d: string): string => {
  const date = new Date(d);
  return `${format(date, '{dd}')}/${format(date, '{MM}')}`;
};

const isRecentPost = (d: string): boolean => {
  const diffMs = Date.now() - new Date(d).getTime();
  return diffMs < 30 * 24 * 60 * 60 * 1000;
};

const fadeUp = {
  hidden: { opacity: 0, y: 8 },
  visible: {
    opacity: 1,
    y: 0,
    transition: { duration: 0.4, ease: [0.16, 1, 0.3, 1] },
  },
};

// The oval strokes itself in, then "New" fades up
const NewBadge = () => {
  const prefersReduced = useReducedMotion();
  return (
    <span
      className="ml-2 relative inline-flex items-center justify-center flex-shrink-0"
      style={{ width: 46, height: 22, transform: 'rotate(-2deg)', verticalAlign: 'middle' }}>
      <svg
        className="absolute inset-0"
        width="46"
        height="22"
        viewBox="0 0 46 22"
        fill="none">
        <motion.path
          d="M5 11C5.5 5 12 1.5 23 2C34 2.5 41.5 5.5 41 11C40.5 16.5 33 20.5 22 20C11 19.5 4.5 17 5 11Z"
          stroke="#ec4899"
          strokeWidth="1.3"
          strokeLinecap="round"
          initial={{ pathLength: prefersReduced ? 1 : 0, opacity: prefersReduced ? 1 : 0 }}
          animate={{ pathLength: 1, opacity: 1 }}
          transition={{
            pathLength: { duration: 0.65, ease: 'easeOut', delay: 0.3 },
            opacity: { duration: 0.01, delay: 0.3 },
          }}
        />
      </svg>
      <motion.span
        className="relative text-xs text-pink-500 leading-none font-normal"
        initial={{ opacity: prefersReduced ? 1 : 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.25, delay: prefersReduced ? 0 : 0.75 }}>
        New
      </motion.span>
    </span>
  );
};

type PostRowProps = {
  post: PostMeta;
  showYear: boolean;
};

const PostRow: React.FC<PostRowProps> = ({ post, showYear }) => {
  const year = new Date(post.date).getFullYear();
  const showBadge = isRecentPost(post.date);

  return (
    <motion.div
      variants={fadeUp}
      className="group flex items-center border-t border-gray-200 dark:border-gray-800 py-2.5">
      <span className="w-14 text-sm text-gray-400 dark:text-gray-500 flex-shrink-0 tabular-nums">
        {showYear ? year : ''}
      </span>
      <Link
        href={`/posts/${post.slug}`}
        className="flex-1 flex items-center min-w-0 text-sm">
        <span className="truncate transition-transform duration-150 ease-out group-hover:translate-x-[3px]">
          {post.title}
        </span>
        {showBadge && <NewBadge />}
      </Link>
      <span className="text-sm text-gray-400 dark:text-gray-500 tabular-nums ml-4 flex-shrink-0 opacity-60 transition-opacity duration-150 group-hover:opacity-100">
        {formatDateShort(post.date)}
      </span>
    </motion.div>
  );
};

const PostList: React.FC<{ posts: PostMeta[] }> = ({ posts }) => {
  const prefersReduced = useReducedMotion();
  const postsByYear: Record<string, PostMeta[]> = {};

  posts.forEach((post) => {
    const year = new Date(post.date).getFullYear().toString();
    postsByYear[year] = [...(postsByYear[year] || []), post];
  });

  const rows = Object.entries(postsByYear)
    .sort(([a], [b]) => Number(b) - Number(a))
    .flatMap(([, yearPosts]) =>
      yearPosts.map((post, idx) => (
        <PostRow key={post.slug} post={post} showYear={idx === 0} />
      ))
    );

  return (
    <motion.div
      className="w-full"
      initial={prefersReduced ? false : 'hidden'}
      animate="visible"
      variants={{
        hidden: {},
        visible: { transition: { staggerChildren: 0.045, delayChildren: 0.05 } },
      }}>
      {rows}
    </motion.div>
  );
};

