    10
    11type ColorTheme = 'light' | 'dark';
    12
    13const ThemeSwitch: React.FC = () => {
    14  const COLOR_THEME = 'theme';
    15
    16  const [mounted, setMounted] = useState(false);
    17  const [theme, setTheme] = useState<ColorTheme>('dark');
    18  const audioRef = useRef<HTMLAudioElement | null>(null);
    19
    20  useEffect(() => {
    21    const currentTheme = (document.body.getAttribute('class') as ColorTheme) || 'dark';
    22    setTheme(currentTheme);
    23    setMounted(true);
    24
    25    if (!audioRef.current) {
    26      const audio = new Audio('/audios/switch.wav');
    27      audioRef.current = audio;
    28    }
    29  }, []);
    30
    31  const switchTheme = () => {
    32    const bodyClass = document.body.classList;
    33
    34    audioRef.current?.play();
    35
    36    if (theme === 'dark') {
    37      setTheme('light');
    38      setLocalStorage<ColorTheme>(COLOR_THEME, 'light');
    39
    40      bodyClass.add('light');
    41      bodyClass.remove('dark');
    42    } else {
    43      setTheme('dark');
    44      setLocalStorage<ColorTheme>(COLOR_THEME, 'dark');
    45
    46      bodyClass.add('dark');
    47      bodyClass.remove('light');
    48    }
    49  };
    50
    51  if (!mounted) {
    52    return <div className="w-11 h-11 flex items-center justify-center" aria-busy="true" aria-label="Loading theme toggle" />;
    53  }
    54
    55  return (
    56    <div className="flex items-center justify-center w-11 h-11 bg-transparent cursor-pointer">
    57      <DarkModeSwitch
    58        checked={theme === 'dark'}
    59        onChange={switchTheme}
    60        moonColor="white"
    61        sunColor="black"
    62        aria-label={`Switch to ${theme === 'dark' ? 'light' : 'dark'} mode`}
    63      />
    64    </div>
    65  );
    66};
    67
    68export default ThemeSwitch;
