    --syntax-background: hsl(60, 0%, 96%);
    --syntax-comment: hsl(0, 0%, 43.5%);
    --syntax-text: hsl(60, 0%, 20%);
    --syntax-operator: hsl(39, 0%, 17.5%);
    --syntax-string: hsl(60, 0%, 35%);
    --syntax-number: hsl(42.3, 0%, 30%);
