.token.symbol,
.token.attr-name,
.token.deleted {
  color: var(--syntax-number);
  font-style: italic;
}

.token.string,
.token.char,
.token.attr-value,
.token.builtin,
.token.inserted {
  color: var(--syntax-string);
}

.token.operator,
.token.entity,
.token.url,
.language-css .token.string,
.style .token.string {
  color: var(--syntax-operator);
}

.token.operator {
  font-weight: 300;
}

.token.atrule,
.token.keyword {
  color: var(--syntax-keyword);
  font-weight: 300;
}

.token.function {
  color: var(--syntax-operator);
  font-weight: 500;
}

.token.regex,
.token.important,
.token.variable {
  color: var(--syntax-keyword);
}

.token.important,
.token.bold {
  font-weight: 500;
}

.token.italic {
  font-style: italic;
}

.token.entity {
  cursor: help;
}

.mdx-marker {
  background: #303240;
  border-left: 3px solid var(--syntax-operator);
  margin-left: calc(-1em - 0.149%);
  margin-right: calc(-1em - 2%);
}

pre.line-numbers {
  position: relative;
  padding-left: 3.8em;
  counter-reset: linenumber;
}

pre.line-numbers > code {
  position: relative;
}

.line-numbers .line-numbers-rows {
  position: absolute;
  pointer-events: none;
  top: 0;
  font-size: 100%;
  left: -3.8em;
  width: 3em; /* works for line-numbers below 1000 lines */
  letter-spacing: -1px;
  border-right: 0;
  user-select: none;
}

.line-numbers-rows > span {
  pointer-events: none;
  display: block;
  counter-increment: linenumber;
}

.line-numbers-rows > span:before {
  content: counter(linenumber);
  color: var(--syntax-comment);
  display: block;
  padding-right: 0.8em;
  text-align: right;
}

