const linkClass =
  'underline underline-offset-2 decoration-gray-300 hover:decoration-current dark:decoration-gray-600 dark:hover:decoration-current transition-colors duration-150';

export const components = {
  a: ({ href = '', ...props }) => {
    if (href.match(/http|https|mailto/)) {
      return (
        <a
          href={href}
          className={linkClass}
          target="_blank"
          rel="noopener noreferrer"
          aria-label={props.children ? `${props.children} (opens in new tab)` : undefined}
          {...props}
        />
      );
    }
    return <Link href={href} passHref {...props} className={linkClass} />;
  },
  img: ({ alt = '', ...props }: { alt?: string; src?: string; children?: React.ReactNode }) => (
