      <Link
        href="/"
        className="flex items-center gap-1.5 text-sm text-gray-400 dark:text-gray-500 hover:opacity-70 transition-opacity duration-150 mb-8 font-moderat">
        <span></span>
        <span>Index</span>
      </Link>
