     3//  --xxx--  <mark>xxx</mark>
     4export default function remarkHighlightSyntax() {
     5  return (tree) => {
     6    visit(tree, 'text', (node, index, parent) => {
     7      if (!parent || index === null) return;
     8
     9      const matches = node.value.match(/--(.+?)--/g);
    10      if (!matches) return;
    11
    12      const parts = node.value.split(/(--.+?--)/g);
    13      const children = parts
    14        .map((part) => {
    15          const highlightMatch = part.match(/^--(.+?)--$/);
    16          if (highlightMatch) {
    17            return {
    18              type: 'mdxJsxTextElement',
    19              name: 'mark',
    20              attributes: [],
    21              children: [
    22                {
    23                  type: 'text',
    24                  value: highlightMatch[1]
    25                }
