    html {
      font-stretch: 25% 150%;
    }
  }

  * {
    scrollbar-color: var(--sc-divider-light) var(--sc-bg);
  }

  ::-webkit-scrollbar {
    width: var(--sc-width);
  }
  ::-webkit-scrollbar:horizontal {
    height: var(--sc-width);
  }
  ::-webkit-scrollbar-track {
    background: var(--sc-bg);
    border-radius: 10px;
