  article blockquote code {
    @apply not-italic;
  }

  article :not(pre) > code {
    background: var(--syntax-background);
    white-space: nowrap;
    font-family: 'Fira Code', monospace;
    @apply rounded-sm mx-1 py-[2px] px-2 text-sm text-stone-800 dark:text-stone-200;
  }
  article :not(pre) > code::before {
    content: '`';
  }

  article :not(pre) > code::after {
    content: '`';
  }

  article blockquote {
    @apply relative py-1 pl-[2ch] pr-[1ch] my-8 dark:bg-[#2a2e31];
    background-color: #fefce8;
    border-inline-start-width: 0 !important;
  }

  article blockquote::before {
    content: '';
    @apply absolute top-0 left-0 inline-block w-1 h-full;
    background-color: #fde047;
  }

  /* article li {
    @apply relative my-3 pl-7;
  }

  article li::before {
    content: '*';
    @apply absolute left-1 opacity-90;
  } */

}

article .anchor {
  padding-left: 0.5rem;
  opacity: 0;
  transition: opacity 300ms ease-in-out;
}

article :is(h2, h3, h4, h4, h6):hover .anchor {
  opacity: 0.6;
}

details {
  padding: 1rem;
  border: 2px solid #F2CFC2;
}

/* css for loading */
@keyframes loading-svg {
  0%   { cy: 10; }
  25%  { cy: 3; }
  50%  { cy: 10; }
}

.loadingCircle {
  animation-duration: 1s;
  animation-iteration-count: infinite;
  animation-name: loading-svg;
}
.loadingCircle:nth-child(2) { animation-delay: .1s }
.loadingCircle:nth-child(3) { animation-delay: .2s }

.fontOutfit {
  font-family: Outfit, ui-monospace, SFMono-Regular, Menlo, Monaco, Consolas, Liberation Mono, Courier New;
}

.hr {
  display: block;
  border: none;
  height: 5px;
  background: url(/images/wave.svg) 0 repeat-x
}

/* eslint-disable @next/next/no-css-tags */
import Head from 'next/head';
import { NextSeo } from 'next-seo';
import { useEffect, useState } from 'react';

import { formateDateFull, validDate } from '@/utils/formatDate';

import { FollowUp } from './FollowUp';
import Subscribe from './Subscribe';

type Props = { meta: PostMeta };

export const PostPage: React.FC<Props> = ({ meta, children }) => {
  const [theme, setTheme] = useState<'light' | 'dark'>('light');

  useEffect(() => {
    const currentTheme = document.body.classList.contains('dark') ? 'dark' : 'light';
    setTheme(currentTheme);

    const observer = new MutationObserver(() => {
      const newTheme = document.body.classList.contains('dark') ? 'dark' : 'light';
      setTheme(newTheme);
    });

    observer.observe(document.body, { attributes: true, attributeFilter: ['class'] });
    return () => observer.disconnect();
  }, []);

  return (
    <>
      <NextSeo
        title={meta.title}
        description={meta.description}
        canonical={`https://www.nazha.co/posts/${meta.slug}`}
        openGraph={{ url: `https://www.nazha.co/posts/${meta.slug}` }}
        additionalMetaTags={[
          { name: 'twitter:title', content: meta.title },
          { name: 'twitter:description', content: meta.description }
        ]}
      />
      <Head>
        <link rel="stylesheet" href="/styles/prism.css" />
      </Head>
      <article className="max-w-[85ch] mx-auto pt-10 pb-5 px-5 prose dark:prose-invert">
        <div>
          <h1 className="mb-1 font-moderat text-[1.75rem] font-semibold capitalize">{meta.title}</h1>
          <div className="flex flex-col pt-6 pb-4 font-moderat text-[0.75rem] font-normal text-black dark:text-white">
            <time dateTime={validDate(meta.date)} className="tracking-[1px]">{formateDateFull(meta.date)}</time>
            {/* {meta.lastUpdateDate ? (
              <time dateTime={validDate(meta.lastUpdateDate)}>
                Last updated on {formateDateFull(meta.lastUpdateDate)}
              </time>
            ) : null} */}
          </div>

          <div className="flex justify-center mb-10 sm:mb-12" aria-hidden="true">
            <div className="hr dark:hr-dark w-64"></div>
          </div>

          {meta.description ? (
            <p className="font-ashbury text-[1.125rem] font-normal leading-[1.625] italic">{meta.description}</p>
          ) : null}
        </div>

        <div className="font-ashbury text-base font-normal leading-[1.625] prose-headings:font-moderat prose-headings:font-semibold">
          {children}
        </div>

        <div className="flex justify-center mt-10 sm:mb-12" aria-hidden="true">
          <div className="hr dark:hr-dark w-64"></div>
        </div>
        {/*  FIXME： empty div is needed. why? */}
      </article>

      <FollowUp meta={meta} />

      <div className="mt-10">
        {/* <Subscribe /> */}
        <iframe
          src={`https://quaily.com/nazha/widget.external?theme=${theme}&list_slug=nazha&layout=subscribe_form&lang=zh`}
          data-theme={theme}
          width="100%"
          height="350px"
          title="Quail Widget"
          className="border-0"
          allow="web-share"
          allowFullScreen></iframe>
      </div>
    </>
  );
};
