import type { SandpackFiles, SandpackPredefinedTemplate } from '@codesandbox/sandpack-react';
import { SandpackCodeEditor, SandpackLayout, SandpackProvider } from '@codesandbox/sandpack-react';
import { githubLight } from '@codesandbox/sandpack-themes';

const SandpackContainer = ({
  template = 'react-ts',
  files
}: {
  template?: SandpackPredefinedTemplate;
  files?: SandpackFiles;
}) => {
  return (
    <SandpackProvider template={template} theme={githubLight} files={files}>
      <SandpackLayout>
        <SandpackCodeEditor showLineNumbers showTabs wrapContent />
      </SandpackLayout>
    </SandpackProvider>
  );
  //   <Sandpack
  //   files={{
  //     "/App.tsx": `export default function App(): JSX.Element {
  //       return <h1>Hello nazha</h1>
  //     }
  //     `,
  //   }}
  //   theme={cyberpunk}
  //   template="react-ts"
  //   options={{
  //       readOnly: false,
  //       showTabs: true,
  //       editorWidthPercentage: 60,
  //       wrapContent: true,
  //       autorun: false,
  //       // showReadOnly: false
  //     }}
  // />
};

export default SandpackContainer;

'use client';

import Link from 'next/link';
import * as React from 'react';

import { Button } from '@/components/ui/button';
import { Icons } from '@/components/ui/icons';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { cn } from '@/utils/cn';

type UserAuthFormProps = React.HTMLAttributes<HTMLDivElement>;

export function UserAuthForm({ className, ...props }: UserAuthFormProps) {
  const [isLoading, setIsLoading] = React.useState<boolean>(false);

  async function onSubmit(event: React.SyntheticEvent) {
    event.preventDefault();
    setIsLoading(true);

    setTimeout(() => {
      setIsLoading(false);
    }, 3000);
  }

  return (
    <div className={cn('grid gap-6', className)} {...props}>
      {/* <form onSubmit={onSubmit}>
        <div className="grid gap-2">
          <div className="grid gap-1">
            <Label className="sr-only" htmlFor="email">
              Email
            </Label>
            <Input
              id="email"
              placeholder="name@example.com"
              type="email"
              autoCapitalize="none"
              autoComplete="email"
              autoCorrect="off"
              disabled={isLoading}
            />
          </div>
          <Button disabled={isLoading}>
            {isLoading && <Icons.spinner className="mr-2 h-4 w-4 animate-spin" />}
            Sign In with Email
          </Button>
        </div>
      </form> */}
      <div className="relative">
        <div className="absolute inset-0 flex items-center">
          <span className="w-full border-t" />
        </div>
        <div className="relative flex justify-center text-xs uppercase">
          <span className="bg-background px-2 text-muted-foreground">continue with</span>
        </div>
      </div>

      <Link href="/api/auth/signin">
        <Button variant="outline" type="button" disabled={isLoading}>
          {isLoading ? (
            <Icons.spinner className="mr-2 h-4 w-4 animate-spin" />
          ) : (
            <Icons.gitHub className="mr-2 h-4 w-4" />
          )}{' '}
          Github
        </Button>
      </Link>

      <Button variant="outline" type="button" disabled={isLoading}>
        {isLoading ? (
          <Icons.spinner className="mr-2 h-4 w-4 animate-spin" />
        ) : (
          <Icons.google className="mr-2 h-4 w-4" />
        )}{' '}
        Google
      </Button>
    </div>
  );
}

