/**
 * Supports plain text, images, quote tweets.
 *
 * Needs support for images, GIFs, and replies maybe?
 * Styles use !important to override Tailwind .prose inside MDX.
 */
export default function Tweet({
  text,
  id,
  author,
  media,
  created_at,
  public_metrics,
  referenced_tweets
}) {
  const authorUrl = `https://twitter.com/${author.username}`;
  const likeUrl = `https://twitter.com/intent/like?tweet_id=${id}`;
  const retweetUrl = `https://twitter.com/intent/retweet?tweet_id=${id}`;
  const replyUrl = `https://twitter.com/intent/tweet?in_reply_to=${id}`;
  const tweetUrl = `https://twitter.com/${author.username}/status/${id}`;
  const createdAt = new Date(created_at);

  const formattedText = text.replace(/https:\/\/[\n\S]+/g, '');

  const quoteTweet = referenced_tweets && referenced_tweets.find((t) => t.type === 'quoted');

  return (
    <div className="tweet m-auto sm:w-3/4 rounded-lg border border-gray-200 dark:border-gray-800 px-6 py-4 my-10 w-full bg-white dark:bg-gray-900">
      <div className="flex items-center">
        <a className="flex h-12 w-12" href={authorUrl} target="_blank" rel="noopener noreferrer">
          <Image
            alt={author.username}
            height={48}
            width={48}
            src={author.profile_image_url}
            className="rounded-full"
          />
        </a>
        <a
          href={authorUrl}
          target="_blank"
          rel="noopener noreferrer"
          className="author flex flex-col ml-4 !no-underline">
