    20→}) => {
    21→  const [headings, setHeadings] = useState<Heading[]>([]);
    22→  const [activeId, setActiveId] = useState('');
    23→
    24→  // Extract headings from the rendered article after mount
    25→  useEffect(() => {
    26→    if (!contentRef.current) return;
    27→    const els = Array.from(contentRef.current.querySelectorAll('h2'));
    28→    setHeadings(
    29→      els.map((el) => ({
    30→        id: el.id,
    31→        // rehype-autolink-headings injects a "#" anchor inside each heading;
    32→        // strip it so TOC labels are clean.
    33→        text: (el.textContent ?? '').replace(/#\s*$/, '').trim(),
    34→        level: 2,
    35→      }))
    36→    );
    37→  // eslint-disable-next-line react-hooks/exhaustive-deps
