    39→
    40→  // Scroll spy: highlight the heading currently at the top of the viewport
    41→  useEffect(() => {
    42→    if (!headings.length) return;
    43→    const handleScroll = () => {
    44→      const scrollY = window.scrollY + 120;
    45→      let current = '';
