   121→  // Inject copy buttons into remark-prism code blocks (raw HTML, not MDX components)
   122→  useEffect(() => {
   123→    const liveRegion = document.createElement('div');
   124→    liveRegion.setAttribute('aria-live', 'polite');
   125→    liveRegion.setAttribute('aria-atomic', 'true');
   126→    liveRegion.className = 'sr-only';
   127→    document.body.appendChild(liveRegion);
   128→
   129→    const blocks = Array.from(document.querySelectorAll<HTMLElement>('.remark-highlight'));
   130→    const cleanups = blocks.map((block) => {
   131→      const btn = document.createElement('button');
   132→      btn.textContent = 'Copy';
   133→      btn.className = 'copy-btn';
   134→
   135→      const handleClick = async () => {
   136→        const code = block.querySelector('code')?.textContent ?? '';
   137→        await navigator.clipboard.writeText(code);
   138→        btn.textContent = 'Copied!';
   139→        liveRegion.textContent = 'Code copied to clipboard';
   140→        setTimeout(() => {
   141→          btn.textContent = 'Copy';
   142→          liveRegion.textContent = '';
   143→        }, 2000);
   144→      };
   145→
   146→      btn.addEventListener('click', handleClick);
   147→      block.appendChild(btn);
   148→
   149→      return () => {
   150→        btn.removeEventListener('click', handleClick);
   151→        if (block.contains(btn)) block.removeChild(btn);
   152→      };
   153→    });
   154→    return () => {
   155→      cleanups.forEach((c) => c());
