     1→import dynamic from 'next/dynamic';
     2→import { useEffect, useRef, useState } from 'react';
     3→
     4→import { setLocalStorage } from '../utils/localStorage';
     5→
     6→const DarkModeSwitch = dynamic(
     7→  () => import('react-toggle-dark-mode').then((mod) => mod.DarkModeSwitch),
     8→  { loading: () => <div className="w-5 h-5" aria-busy="true" aria-label="Loading theme toggle"></div> }
     9→);
    10→
    11→type ColorTheme = 'light' | 'dark';
    12→
    13→const ThemeSwitch: React.FC = () => {
    14→  const COLOR_THEME = 'theme';
    15→
    16→  const [mounted, setMounted] = useState(false);
    17→  const [theme, setTheme] = useState<ColorTheme>('dark');
    18→  const audioRef = useRef<HTMLAudioElement | null>(null);
    19→
    20→  useEffect(() => {
    21→    const currentTheme = (document.body.getAttribute('class') as ColorTheme) || 'dark';
    22→    setTheme(currentTheme);
    23→    setMounted(true);
    24→
    25→    if (!audioRef.current) {
    26→      const audio = new Audio('/audios/switch.wav');
    27→      audioRef.current = audio;
    28→    }
    29→  }, []);
    30→
    31→  const switchTheme = () => {
    32→    const bodyClass = document.body.classList;
    33→
    34→    audioRef.current?.play();
    35→
    36→    if (theme === 'dark') {
    37→      setTheme('light');
    38→      setLocalStorage<ColorTheme>(COLOR_THEME, 'light');
    39→
    40→      bodyClass.add('light');
    41→      bodyClass.remove('dark');
    42→    } else {
    43→      setTheme('dark');
    44→      setLocalStorage<ColorTheme>(COLOR_THEME, 'dark');
    45→
    46→      bodyClass.add('dark');
