     1→import { ReactNode, useEffect, useRef, useState } from 'react';
     2→import { createPortal } from 'react-dom';
     3→
     4→const URL_RE = /(https?:\/\/[a-zA-Z0-9\-._~:/?#[\]@!$&'()*+,;=%]+)/;
     5→
     6→function linkify(text: string): ReactNode[] {
     7→  const parts = text.split(URL_RE);
     8→  return parts.map((part, i) =>
     9→    i % 2 === 1
    10→      ? <a key={i} href={part} target="_blank" rel="noopener noreferrer">{part}</a>
    11→      : part
    12→  );
    13→}
    14→
    15→function linkifyChildren(children: ReactNode): ReactNode {
    16→  if (typeof children !== 'string') return children;
    17→  return children.split('\\n').flatMap((line, i, arr) => {
    18→    const linked = linkify(line);
    19→    return i < arr.length - 1 ? [...linked, <br key={`br-${i}`} />] : linked;
    20→  });
    21→}
    22→
    23→interface LineCoords { x1: number; y1: number; x2: number; y2: number; x2End: number }
    24→
    25→function ConnectedSideNote({ children, note }: { children: ReactNode; note: string }) {
    26→  const refEl = useRef<HTMLSpanElement>(null);
    27→  const noteEl = useRef<HTMLSpanElement>(null);
    28→  const [line, setLine] = useState<LineCoords | null>(null);
    29→  const [hovered, setHovered] = useState(false);
    30→  const [mounted, setMounted] = useState(false);
    31→
    32→  useEffect(() => { setMounted(true); }, []);
    33→
    34→  useEffect(() => {
    35→    if (!mounted) return;
    36→    const measure = () => {
    37→      if (!refEl.current || !noteEl.current || window.innerWidth < 1280) {
    38→        setLine(null);
    39→        return;
    40→      }
    41→      const r = refEl.current.getBoundingClientRect();
    42→      const n = noteEl.current.getBoundingClientRect();
    43→      setLine({ x1: r.right, y1: r.bottom, x2: n.left, y2: n.bottom, x2End: n.right });
    44→    };
    45→    measure();
    46→    window.addEventListener('resize', measure);
    47→    window.addEventListener('scroll', measure, { passive: true });
    48→    return () => {
    49→      window.removeEventListener('resize', measure);
    50→      window.removeEventListener('scroll', measure);
    51→    };
    52→  }, [mounted]);
    53→
    54→  const onEnter = () => setHovered(true);
    55→  const onLeave = () => setHovered(false);
    56→
    57→  return (
    58→    <>
    59→      <span ref={refEl} className={`footnote-ref${hovered ? ' footnote-ref-boxed' : ''}`} onMouseEnter={onEnter} onMouseLeave={onLeave}>
    60→        {children}
    61→      </span>
    62→      <span ref={noteEl} className="sidenote sidenote-no-border" onMouseEnter={onEnter} onMouseLeave={onLeave}>
    63→        {linkifyChildren(note)}
    64→      </span>
    65→      {mounted && line && createPortal(
    66→        <svg
    67→          aria-hidden="true"
    68→          style={{ position: 'fixed', inset: 0, width: '100vw', height: '100vh', pointerEvents: 'none', zIndex: 5, overflow: 'visible' }}
    69→        >
    70→          <g
    71→            stroke="currentColor"
    72→            strokeWidth={0.75}
    73→            fill="none"
    74→            style={{ opacity: hovered ? 0.5 : 0.15, transition: 'opacity 200ms ease' }}
    75→          >
    76→            {/* horizontal → down left edge of sidenote → baseline right */}
    77→            <path d={`M ${line.x1} ${line.y1} L ${line.x2} ${line.y1} L ${line.x2} ${line.y2} L ${line.x2End} ${line.y2}`} />
    78→          </g>
    79→        </svg>,
    80→        document.body
    81→      )}
    82→    </>
    83→  );
    84→}
    85→
    86→interface SideNoteProps {
    87→  children: ReactNode;
    88→  anchor?: ReactNode;
    89→  asChild?: boolean;
    90→  note?: string;
    91→}
    92→
    93→export function SideNote({ children, anchor, asChild, note }: SideNoteProps) {
    94→  if (asChild && note) {
    95→    return <ConnectedSideNote note={note}>{children}</ConnectedSideNote>;
    96→  }
    97→  return (
    98→    <>
    99→      {anchor && <span className="footnote-ref">{anchor}</span>}
   100→      <span className="sidenote">{linkifyChildren(children)}</span>
   101→    </>
   102→  );
   103→}
   104→

